import "core-js/modules/es.array.push.js";
// require('@fullcalendar/core/main.min.css')

import FullCalendar from '@fullcalendar/vue3';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import listPlugin from '@fullcalendar/list';
import axios from 'axios';
// import { INITIAL_EVENTS, createEventId } from './event-utils'
import bootstrap5Plugin from '@fullcalendar/bootstrap5';
let eventGuid = 0;
export function createEventId() {
  return String(eventGuid++);
}
function formatDatetime(datetimeStr) {
  return datetimeStr.replace('T', ' ').replace(/\+.*$/, '');
}
export default {
  components: {
    FullCalendar // make the <FullCalendar> tag available
  },
  data: function () {
    return {
      calendarOptions: {
        //1 day only
        selectAllow: function (selectionInfo) {
          let startDate = selectionInfo.start;
          let endDate = selectionInfo.end;
          endDate.setSeconds(endDate.getSeconds() - 1); // allow full day selection
          if (startDate.getDate() === endDate.getDate()) {
            return true;
          } else {
            return false;
          }
        },
        plugins: [bootstrap5Plugin, listPlugin, dayGridPlugin, timeGridPlugin, interactionPlugin // needed for dateClick
        ],
        headerToolbar: {
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay,listMonth'
        },
        themeSystem: 'bootstrap5',
        // important!
        initialView: 'dayGridMonth',
        // initialEvents: INITIAL_EVENTS, // alternatively, use the `events` setting to fetch from a feed
        editable: true,
        selectable: true,
        selectMirror: true,
        dayMaxEvents: true,
        weekends: true,
        select: this.handleDateSelect,
        eventClick: this.deleteItem,
        eventsSet: this.handleEvents,
        eventDrop: this.handleEventDrop,
        // Add eventDrop handler
        eventResize: this.handleEventResize

        /* you can update a remote database when these fire:
        eventAdd:
        eventChange:
        eventRemove:
        */
      },
      allevents: []
    };
  },
  mounted() {
    this.fetchEvents();
  },
  methods: {
    fetchEvents() {
      // Fetch events from local storage
      let eventsFromLocalStorage = JSON.parse(localStorage.getItem('events')) || [];

      // Fetch events from the API
      axios.get('http://localhost:8000/api/events').then(response => {
        let eventsFromAPI = response.data.filter(event => event.user === 'admin').map(event => {
          let color;
          switch (event.user) {
            case 'clientApproval':
              color = '#007FFF';
              break;
            case 'clientApproved':
              color = '#FF9E00';
              break;
            case 'admin':
              color = '#FF2D00';
              break;
            default:
              // Handle default case if needed
              break;
          }
          return {
            id: event.id,
            title: event.title,
            user: event.user,
            start: event.start,
            end: event.end,
            allDay: event.allDay,
            color: color // Assign color based on user type
          };
        });

        // Update local events with API events and update user if IDs match
        eventsFromLocalStorage.forEach(localEvent => {
          let correspondingAPIEvent = response.data.find(apiEvent => apiEvent.id === localEvent.id);
          if (correspondingAPIEvent) {
            if (correspondingAPIEvent.user !== localEvent.user) {
              console.log("Delete Approved clients:::", correspondingAPIEvent.id, correspondingAPIEvent.user, "  :  ", localEvent.id, localEvent.user);
              const updatedEvents = eventsFromLocalStorage.filter(event => event.id !== localEvent.id);
              console.log('Updated events after deletion:', updatedEvents);
              // Store the updated events back into local storage
              localStorage.setItem('events', JSON.stringify(updatedEvents));
              this.fetchEvents();
            }
          }
        });
        // console.log('Events before deletion:', eventsFromLocalStorage);
        let allEvents = eventsFromAPI.concat(eventsFromLocalStorage);
        this.calendarOptions.events = allEvents; // Directly assign events to calendarOptions
      }).catch(error => {
        console.error('Error fetching events:', error);
      });
    },
    handleWeekendsToggle() {
      this.calendarOptions.weekends = !this.calendarOptions.weekends; // update a property
    },
    //ADD EVENT
    handleDateSelect(selectInfo) {
      let title = prompt('Please enter a new title for your event');
      let calendarApi = selectInfo.view.calendar;
      if (title) {
        // Format start and end dates
        let start = formatDatetime(selectInfo.startStr);
        let end = formatDatetime(selectInfo.endStr);

        // Check if the selected time slot is already occupied by an event added by the admin
        axios.get('http://localhost:8000/api/events', {
          params: {
            start: start,
            end: end,
            user: 'admin'
          }
        }).then(response => {
          // If admin event found within the selected time range, show modal
          if (response.data.length > 0) {
            alert("The day is already set by admin busy please choose another day to schedule your appointment");
          } else {
            // Proceed to add the event if no conflicting admin event found
            calendarApi.unselect(); // Clear date selection

            axios.post('http://localhost:8000/api/events', {
              title: title,
              start: start,
              end: end,
              user: 'clientApproval',
              allDay: selectInfo.allDay
            }).then(response => {
              // Handle success
              console.log('Event added:', response.data);
              this.allevents.push(response.data);
              let newEvent = {
                id: response.data.id,
                title: response.data.title,
                user: response.data.user,
                start: response.data.start,
                end: response.data.end,
                allDay: response.data.allDay
              };

              // Get existing events from local storage or initialize an empty array
              let events = JSON.parse(localStorage.getItem('events')) || [];
              events.push(newEvent); // Append the new event to existing events
              localStorage.setItem('events', JSON.stringify(events)); // Set the updated events array to local storage

              calendarApi.addEvent(newEvent);
              // Fetch events again to update the calendar
              this.fetchEvents();
            }).catch(error => {
              // Handle error
              console.error('Error adding event:', error.response.data);
            });
          }
        }).catch(error => {
          console.error('Error checking admin events:', error);
        });
      }
    },
    deleteItem(clickInfo) {
      console.log("Deleted? : " + clickInfo.event.id);
      if (confirm(`Are you sure you want to delete the event '${clickInfo.event.title}'`)) {
        axios.delete(`http://localhost:8000/api/events/${clickInfo.event.id}`).then(response => {
          // this.fetchEvents(); // Refresh events after deleting
          console.log("Succesfully Deleted: " + response.data + clickInfo.event.id);

          // Fetch events from local storage
          let events = JSON.parse(localStorage.getItem('events')) || [];
          // console.log('Events before deletion:', events);
          // Filter out the event to be deleted
          const updatedEvents = events.filter(event => String(event.id) !== String(clickInfo.event.id));
          console.log('Updated events after deletion:', updatedEvents);
          // Store the updated events back into local storage
          localStorage.setItem('events', JSON.stringify(updatedEvents));
          this.fetchEvents();
        }).catch(error => {
          console.error('Error deleting event:', error);
        });
      }
    },
    handleEventDrop(info) {
      const eventId = info.event.id;
      const allDay = info.event.allDay;
      const start_formatdate = formatDatetime(info.event.startStr);
      let end_formatdate = formatDatetime(info.event.endStr);

      // If end date is empty, set it to start date
      if (end_formatdate === "") {
        end_formatdate = start_formatdate;
      }
      console.log("handleEventDrop : ", eventId, "start_formatdate: " + start_formatdate, "End: " + end_formatdate, "AllDay: ", allDay);
      axios.put(`http://localhost:8000/api/events/${eventId}`, {
        start: start_formatdate,
        end: end_formatdate,
        allDay: allDay
      }).then(response => {
        console.log('Event updated:', response.data);

        // Retrieve events from local storage
        let events = JSON.parse(localStorage.getItem('events')) || [];
        console.log('Existing events:', events);

        // Find index of event being updated
        const eventIndex = events.findIndex(event => event.id === eventId);
        console.log('Event index:', eventIndex);

        // Update event if found
        if (eventIndex !== -1) {
          events[eventIndex].start = response.data.start;
          events[eventIndex].end = response.data.end;

          // Save updated events to local storage
          localStorage.setItem('events', JSON.stringify(events));
          console.log('Updated events:', events);
        } else {
          // If event is not found, it might be a new event, handle accordingly
          console.log('Event not found in local storage. Adding new event...');
          events.push(response.data);
          localStorage.setItem('events', JSON.stringify(events));
          console.log('Updated events:', events);
        }
      }).catch(error => {
        console.error('Error updating event:', error.response.data);
      });
    },
    // Function to handle resize event
    handleEventResize(info) {
      const eventId = info.event.id;
      const start = formatDatetime(info.event.startStr);
      const end = formatDatetime(info.event.endStr);
      console.log("handleEventResize : ", eventId, "start ", start, "  end ", end);
      axios.put(`http://localhost:8000/api/events/${eventId}`, {
        start: start,
        end: end,
        allDay: false
      }).then(response => {
        console.log('Event updated Resize:', response.data);
      }).catch(error => {
        console.error('Error updating event:', error.response.data);
      });
    },
    handleEvents(events) {
      this.allevents = events;
    }
  }
};
